   var isOpen = true;
function createOrders(items){
    return new Promise((resolve , reject) => {
         alert("order Created")
        console.log("order created");
        setTimeout(() =>{
            if(isOpen){
                resolve(items);
            }else {
                reject("Restaurent is Closed !")
            }
           
        } , 2000)
    });
}
