var movies = JSON.parse(localStorage.getItem("movies"))

// var sortedMovies =  JSON.parse(JSON.stringify(movies));
var sortedMovies =  [... movies];

selectedVal = document.getElementById("sortByRating");
selectedVal.addEventListener("change", function(){

    if(selectedVal.value == ""){
        displayMovie(movies);
    
    }else {
        sortedMovies.sort(function(a , b){
            if(selectedVal.value === "LH"){
                
                return Number(a.IMDB) - Number(b.IMDB);
            }else {  
            
                return Number(b.IMDB) - Number(a.IMDB);
            }
        })

        displayMovie(sortedMovies);
    }
})

function displayMovie(movies){  

    document.getElementById("movieContainer").innerText = "";
movies.forEach(function(movie){
     var movieCart = document.createElement("div");
       var imgDiv = document.createElement("div");
          var img = document.createElement("img");
            img.src = movie.poster;
       imgDiv.append(img);
       var name = document.createElement("div");
       name.textContent = movie.name;
       var release = document.createElement("div");
        release. textContent = `Release Date :  ${movie.release}`;
        var imdbRating = document.createElement("div");
         imdbRating.textContent = `IMDB Rating : ${movie.IMDB}`;

        movieCart.append(imgDiv , name , release , imdbRating);

        document.getElementById("movieContainer").append(movieCart);
})

}

displayMovie(movies);

// search functionality
var searchedMovie = document.querySelector("#searchContainer input");

searchedMovie.addEventListener("change", function(){
    var filteredMovies = movies.filter(function(movie){
       if(movie.name.includes(searchedMovie.value)){
           return true;
       }else {
            return false ;
       }
    })
    displayMovie(filteredMovies);
})